package Lecture4;

public class Selection3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day = 2;
		if(day == 1) {
			System.out.println("Monday");
		} else if(day == 2) {
			System.out.println("Tuesday");
		} else if(day == 3) {
			System.out.println("Wednesday");
		}else {
			System.out.println("No day");
		}
	}

}
